tinymce.PluginManager.add('component', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('component', {
        text: 'Component',
        icon: "code",
        onclick: function() {
            var cValues = null;
            $.ajax({
                url: '/manager/editor/components',
                async: false,
                type: 'post',
                dataType: 'json',
                success: function (output) {
                    cValues = output;
                }
            });
            console.log(cValues);
            // Open window
            editor.windowManager.open({
                title: 'Insert component',
                body: [
                    {type: 'listbox', name: 'component', text: 'Component', icon: false, values: cValues}
                ],
                onsubmit: function(e) {
                    // Insert content when the window form is submitted
                    editor.insertContent("[component id='" + e.data.component + "']");
                }
            });
        }
    });
});