tinymce.PluginManager.add('galleryalbum', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('galleryalbum', {
        text: 'Album',
        icon: "image",
        onclick: function() {
            var cValues = null;
            $.ajax({
                url: '/manager/editor/galleryalbums',
                async: false,
                type: 'post',
                dataType: 'json',
                success: function (output) {
                    cValues = output;
                }
            });
            console.log(cValues);
            // Open window
            editor.windowManager.open({
                title: 'Insert gallery album',
                body: [
                    {type: 'listbox', name: 'galleryalbum', text: 'Album', icon: false, values: cValues}
                ],
                onsubmit: function(e) {
                    // Insert content when the window form is submitted
                    editor.insertContent("[galleryalbum id='" + e.data.galleryalbum + "']");
                }
            });
        }
    });
});