tinymce.PluginManager.add('module', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('module', {
        text: 'Module',
        icon: "code",
        onclick: function() {
            var cValues = null;
            $.ajax({
                url: '/manager/editor/modules',
                async: false,
                type: 'post',
                dataType: 'json',
                success: function (output) {
                    cValues = output;
                }
            });
            console.log(cValues);
            // Open window
            editor.windowManager.open({
                title: 'Insert module',
                body: [
                    {type: 'listbox', name: 'module', text: 'Module', icon: false, values: cValues}
                ],
                onsubmit: function(e) {
                    // Insert content when the window form is submitted
                    editor.insertContent("[module id='" + e.data.module + "']");
                }
            });
        }
    });
});