tinymce.PluginManager.add('videoalbum', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('videoalbum', {
        text: 'Album',
        icon: "media",
        onclick: function() {
            var cValues = null;
            $.ajax({
                url: '/manager/editor/videoalbums',
                async: false,
                type: 'post',
                dataType: 'json',
                success: function (output) {
                    cValues = output;
                }
            });
            console.log(cValues);
            // Open window
            editor.windowManager.open({
                title: 'Insert video album',
                body: [
                    {type: 'listbox', name: 'videoalbum', text: 'Album', icon: false, values: cValues}
                ],
                onsubmit: function(e) {
                    // Insert content when the window form is submitted
                    editor.insertContent("[videoalbum id='" + e.data.videoalbum + "']");
                }
            });
        }
    });
});