tinymce.PluginManager.add('videoclip', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('videoclip', {
        text: 'Clip',
        icon: "media",
        onclick: function() {
            var cValues = null;
            $.ajax({
                url: '/manager/editor/videoclips',
                async: false,
                type: 'post',
                dataType: 'json',
                success: function (output) {
                    cValues = output;
                }
            });
            console.log(cValues);
            // Open window
            editor.windowManager.open({
                title: 'Insert video clip',
                body: [
                    {type: 'listbox', name: 'videoclip', text: 'Clip', icon: false, values: cValues}
                ],
                onsubmit: function(e) {
                    // Insert content when the window form is submitted
                    editor.insertContent("[videoclip id='" + e.data.videoclip + "']");
                }
            });
        }
    });
});